-- Order Header and Order Detail Data
DECLARE @i INT = 1;

WHILE @i <= 100
BEGIN
    DECLARE @user_id INT = FLOOR(RAND() * 50) + 1; -- Random User ID
    DECLARE @restaurant_id INT = FLOOR(RAND() * 10) + 1; -- Random Restaurant ID
    DECLARE @delivery_person_id INT = FLOOR(RAND() * 10) + 1; -- Random Delivery Person ID
    DECLARE @order_id INT = @i;

    INSERT INTO OrderHeader (order_date, total_amount, delivery_time, status, user_id, restaurant_id, delivery_person_id)
    VALUES (GETDATE(), 50 + FLOOR(RAND() * 100), CAST(FLOOR(RAND() * 12) + 12 AS VARCHAR) + ':00:00', 'Delivered', @user_id, @restaurant_id, @delivery_person_id);

    DECLARE @j INT = 1;
	DECLARE @k INT = FLOOR(RAND() * 3) + 1;
	DECLARE @total_amount DECIMAL(10,2) = 0.00;
    WHILE @j <= @k -- Add k items per order
    BEGIN
        DECLARE @food_id INT = (SELECT TOP 1 food_id FROM dbo.Food WHERE restaurant_id = @restaurant_id ORDER BY NEWID()); -- Random Food ID
        DECLARE @quantity INT = FLOOR(RAND() * 3) + 1;
        DECLARE @subtotal DECIMAL(10, 2) = (SELECT price FROM Food WHERE food_id = @food_id) * @quantity;
		SET @total_amount = @total_amount + @subtotal;
        INSERT INTO OrderDetail (order_id, food_id, quantity, subtotal)
        VALUES (@order_id, @food_id, @quantity, @subtotal);

        SET @j = @j + 1;
    END;
	UPDATE OrderHeader SET total_amount = @total_amount WHERE order_id = @order_id;
    SET @i = @i + 1;
END;


